package basicPrograms;

public class OddEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<=10;i++)
		{
			if(i%2==0)
				System.out.println("Even Number: "+i);
		}
		
		
				
	}

}
/*
Output:
	Even Number: 0
	Even Number: 2
	Even Number: 4
	Even Number: 6
	Even Number: 8
	Even Number: 10
	
*/
